import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";


const carouselImages = [
  { src: "/images/image1home.jpeg", alt: "Welcome to BlogBuzz" },
  { src: "/images/image2blog.jpeg", alt: "Share Your Stories" },
  { src: "/images/image3blog.jpeg", alt: "Connect with Others" }
];

export default function Home() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchBlogs();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % carouselImages.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const fetchBlogs = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/blogs");
      const data = await res.json();
      setBlogs(data);
    } catch (error) {
      console.error("Error fetching blogs:", error);
    } finally {
      setLoading(false);
    }
  };

  // Search blogs function
  const handleSearch = async (query) => {
    setSearchQuery(query);
    setLoading(true);
    try {
      const url = query.trim()
        ? `http://localhost:5000/api/blogs/search?query=${encodeURIComponent(query)}`
        : "http://localhost:5000/api/blogs";
      const res = await fetch(url);
      const data = await res.json();
      setBlogs(data);
    } catch (error) {
      console.error("Error searching blogs:", error);
    } finally {
      setLoading(false);
    }
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % carouselImages.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + carouselImages.length) % carouselImages.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const getShortDescription = (text, maxLength = 150) => {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + "...";
  };

  if (loading) {
    return <div style={styles.loading}>Loading blogs...</div>;
  }

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1>‚úçüèºBlogBuzz</h1>
        <div style={styles.headerButtons}>
          {user ? (
            <>
              <button onClick={() => navigate("/add-blog")} style={styles.addBtn}>
                + Add Blog
              </button>
              <button onClick={() => navigate("/dashboard")} style={styles.btn}>
                Dashboard
              </button>
            </>
          ) : (
            <>
              <Link to="/login">
                <button style={styles.btn}>Login</button>
              </Link>
              <Link to="/register">
                <button style={styles.btn}>Signup</button>
              </Link>
            </>
          )}
        </div>
      </header>
      <div style={styles.carousel} className="carousel-container">
        <div style={styles.carouselInner}>
          <img
            src={carouselImages[currentSlide].src}
            alt={carouselImages[currentSlide].alt}
            style={styles.carouselImage}
          />
          <div style={styles.carouselOverlay}>
            <h2 style={styles.carouselTitle}>{carouselImages[currentSlide].alt}</h2>
          </div>
        </div>
        <button onClick={prevSlide} style={styles.carouselBtnLeft}>
          &#10094;
        </button>
        <button onClick={nextSlide} style={styles.carouselBtnRight}>
          &#10095;
        </button>
        <div style={styles.dotsContainer}>
          {carouselImages.map((_, index) => (
            <span
              key={index}
              style={index === currentSlide ? styles.dotActive : styles.dot}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>

      <main style={styles.main}>
        <div style={styles.searchSection}>
          <h2>All Blogs</h2>
          <div style={styles.searchBox}>
            <input
              type="text"
              placeholder="Search blogs..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
              style={styles.searchInput}
            />
            {searchQuery && (
              <button
                onClick={() => handleSearch("")}
                style={styles.clearBtn}
              >
                Clear
              </button>
            )}
          </div>
        </div>

        {loading ? (
          <p style={styles.searchStatus}>Searching...</p>
        ) : blogs.length === 0 ? (
          <p style={styles.noResults}>
            {searchQuery
              ? `No blogs found for "${searchQuery}"`
              : "No blogs yet. Be the first to create one!"}
          </p>
        ) : (
          <div style={styles.blogList}>
            {blogs.map(blog => (
              <div
                key={blog._id}
                style={styles.blogCard}
                onClick={() => navigate(`/blog/${blog._id}`)}
              >
                <p style={styles.description}>
                  {getShortDescription(blog.description)}
                </p>
                <div style={styles.blogMeta}>
                  <span style={styles.author}>By: {blog.authorId?.name || "Unknown"}</span>
                  <div style={styles.stats}>
                    <span style={styles.stat}>
                      {blog.likes?.length || 0} Likes
                    </span>
                    <span style={styles.stat}>
                      {blog.comments?.length || 0} Comments
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "900px",
    margin: "0 auto",
    padding: "20px"
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "30px",
    borderBottom: "1px solid #ddd",
    paddingBottom: "15px"
  },
  headerButtons: {
    display: "flex",
    gap: "10px"
  },
  btn: {
    padding: "10px 20px",
    cursor: "pointer",
    border: "1px solid #333",
    background: "#fff"
  },
  addBtn: {
    padding: "10px 20px",
    cursor: "pointer",
    border: "none",
    background: "#28a745",
    color: "#fff"
  },
  
  carousel: {
    position: "relative",
    width: "100%",
    height: "240px",
    marginTop: "0",
    marginBottom: "20px",
    borderRadius: "10px",
    overflow: "hidden",
    boxShadow: "0 2px 10px rgba(0,0,0,0.15)"
  },
  carouselInner: {
    width: "100%",
    height: "100%",
    position: "relative",
    display: "block",
    lineHeight: "0"
  },
  carouselImage: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    objectPosition: "center",
    display: "block",
    transition: "opacity 0.5s ease-in-out"
  },
  carouselOverlay: {
    position: "absolute",
    bottom: "0",
    left: "0",
    right: "0",
    background: "linear-gradient(transparent, rgba(0,0,0,0.6))",
    padding: "20px 15px 12px",
    color: "#fff"
  },
  carouselTitle: {
    margin: "0",
    fontSize: "20px",
    fontWeight: "600",
    textShadow: "1px 1px 3px rgba(0,0,0,0.5)"
  },
  carouselBtnLeft: {
    position: "absolute",
    top: "50%",
    left: "10px",
    transform: "translateY(-50%)",
    background: "rgba(255,255,255,0.85)",
    border: "none",
    padding: "10px 13px",
    fontSize: "16px",
    cursor: "pointer",
    borderRadius: "50%",
    transition: "background 0.3s",
    lineHeight: "1"
  },
  carouselBtnRight: {
    position: "absolute",
    top: "50%",
    right: "10px",
    transform: "translateY(-50%)",
    background: "rgba(255,255,255,0.85)",
