import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function Dashboard() {
  const [blogs, setBlogs] = useState([]);
  const [savedBlogs, setSavedBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("my-blogs");
  const { user, logout, checkAuth } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchMyBlogs();
    fetchSavedBlogs();
  }, []);

  const fetchMyBlogs = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/blogs/user/my-blogs", {
        credentials: "include"
      });
      if (res.ok) {
        const data = await res.json();
        setBlogs(data);
      }
    } catch (error) {
      console.error("Error fetching blogs:", error);
    } finally {
      setLoading(false);
    }
  };

  const fetchSavedBlogs = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/blogs/user/saved", {
        credentials: "include"
      });
      if (res.ok) {
        const data = await res.json();
        setSavedBlogs(data);
      }
    } catch (error) {
      console.error("Error fetching saved blogs:", error);
    }
  };

  const handleDelete = async (blogId) => {
    if (!window.confirm("Are you sure you want to delete this blog?")) {
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/api/blogs/${blogId}`, {
        method: "DELETE",
        credentials: "include"
      });

      if (res.ok) {
        setBlogs(blogs.filter(b => b._id !== blogId));
      } else {
        const data = await res.json();
        alert(data.message || "Failed to delete blog");
      }
    } catch (error) {
      console.error("Error deleting blog:", error);
    }
  };

  const handleUnsave = async (blogId) => {
    try {
      const res = await fetch(`http://localhost:5000/api/blogs/${blogId}/save`, {
        method: "POST",
        credentials: "include"
      });

      if (res.ok) {
        setSavedBlogs(savedBlogs.filter(b => b._id !== blogId));
        checkAuth();
      }
    } catch (error) {
      console.error("Error unsaving blog:", error);
    }
  };

  const handleLogout = async () => {
    await logout();
    navigate("/");
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric"
    });
  };

  if (loading) {
    return <div style={styles.loading}>Loading...</div>;
  }

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1>Dashboard</h1>
        <div style={styles.headerButtons}>
          <button onClick={() => navigate("/")} style={styles.btn}>
            Home
          </button>
          <button onClick={() => navigate("/chat")} style={styles.chatBtn}>
            Community Chat
          </button>
          <button onClick={handleLogout} style={styles.logoutBtn}>
            Logout
          </button>
        </div>
      </header>

      <section style={styles.userSection}>
        <h2>User Details</h2>
        <div style={styles.userCard}>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
          <p><strong>Member since:</strong> {user?.createdAt ? formatDate(user.createdAt) : "N/A"}</p>
        </div>
      </section>

      <div style={styles.tabs}>
        <button
          style={activeTab === "my-blogs" ? styles.activeTab : styles.tab}
          onClick={() => setActiveTab("my-blogs")}
        >
          My Blogs ({blogs.length})
        </button>
        <button
          style={activeTab === "saved" ? styles.activeTab : styles.tab}
          onClick={() => setActiveTab("saved")}
        >
          Saved Blogs ({savedBlogs.length})
        </button>
      </div>

      {activeTab === "my-blogs" && (
        <section style={styles.blogsSection}>
          <div style={styles.sectionHeader}>
            <h2>My Blogs</h2>
            <button onClick={() => navigate("/add-blog")} style={styles.addBtn}>
              + Add New Blog
            </button>
          </div>

          {blogs.length === 0 ? (
            <p style={styles.noBlogs}>
              You haven't created any blogs yet.
              <button onClick={() => navigate("/add-blog")} style={styles.linkBtn}>
                Create your first blog
              </button>
            </p>
          ) : (
            <div style={styles.blogList}>
              {blogs.map(blog => (
                <div key={blog._id} style={styles.blogCard}>
                  <div
                    style={styles.blogContent}
                    onClick={() => navigate(`/blog/${blog._id}`)}
                  >
                    <p style={styles.description}>
                      {blog.description.length > 200
                        ? blog.description.substring(0, 200) + "..."
                        : blog.description}
                    </p>
                    <div style={styles.blogMeta}>
                      <span>{formatDate(blog.createdAt)}</span>
                      <span>{blog.likes?.length || 0} Likes</span>
                      <span>{blog.comments?.length || 0} Comments</span>
                    </div>
                  </div>
                  <div style={styles.blogActions}>
                    <button
                      onClick={() => navigate(`/blog/${blog._id}`)}
                      style={styles.viewBtn}
                    >
                      View
                    </button>
                    <button
                      onClick={() => navigate(`/edit-blog/${blog._id}`)}
                      style={styles.editBtn}
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDelete(blog._id)}
                      style={styles.deleteBtn}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      )}

      {activeTab === "saved" && (
        <section style={styles.blogsSection}>
          <div style={styles.sectionHeader}>
            <h2>Saved Blogs</h2>
          </div>
          {savedBlogs.length === 0 ? (
            <p style={styles.noBlogs}>
              You haven't saved any blogs yet.
              <button onClick={() => navigate("/")} style={styles.linkBtn}>
                Browse blogs
              </button>
            </p>
          ) : (
            <div style={styles.blogList}>
              {savedBlogs.map(blog => (
                <div key={blog._id} style={styles.blogCard}>
                  <div
                    style={styles.blogContent}
                    onClick={() => navigate(`/blog/${blog._id}`)}
                  >
                    <p style={styles.description}>
                      {blog.description.length > 200
                        ? blog.description.substring(0, 200) + "..."
                        : blog.description}
                    </p>
                    <div style={styles.blogMeta}>
                      <span>By: {blog.authorId?.name || "Unknown"}</span>
                      <span>{blog.likes?.length || 0} Likes</span>
                      <span>{blog.comments?.length || 0} Comments</span>
                    </div>
                  </div>
                  <div style={styles.blogActions}>
                    <button
                      onClick={() => navigate(`/blog/${blog._id}`)}
                      style={styles.viewBtn}
                    >
                      View
                    </button>
                    <button
                      onClick={() => handleUnsave(blog._id)}
                      style={styles.unsaveBtn}
                    >
                      Unsave
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </section>
      )}
    </div>
  );
}

