import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function Home() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchBlogs();
  }, []);

  const fetchBlogs = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/blogs");
      const data = await res.json();
      setBlogs(data);
    } catch (error) {
      console.error("Error fetching blogs:", error);
    } finally {
      setLoading(false);
    }
  };

  // Truncate description for short preview
  const getShortDescription = (text, maxLength = 150) => {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + "...";
  };

  if (loading) {
    return <div style={styles.loading}>Loading blogs...</div>;
  }

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1>Blog App</h1>
        <div style={styles.headerButtons}>
          {user ? (
            <>
              <button onClick={() => navigate("/add-blog")} style={styles.addBtn}>
                + Add Blog
              </button>
              <button onClick={() => navigate("/dashboard")} style={styles.btn}>
                Dashboard
              </button>
            </>
          ) : (
            <>
              <Link to="/login">
                <button style={styles.btn}>Login</button>
              </Link>
              <Link to="/register">
                <button style={styles.btn}>Signup</button>
              </Link>
            </>
          )}
        </div>
      </header>

      <main style={styles.main}>
        <h2>All Blogs</h2>

        {blogs.length === 0 ? (
          <p>No blogs yet. Be the first to create one!</p>
        ) : (
          <div style={styles.blogList}>
            {blogs.map(blog => (
              <div
                key={blog._id}
                style={styles.blogCard}
                onClick={() => navigate(`/blog/${blog._id}`)}
              >
                <p style={styles.description}>
                  {getShortDescription(blog.description)}
                </p>
                <div style={styles.blogMeta}>
                  <span style={styles.author}>By: {blog.authorId?.name || "Unknown"}</span>
                  <div style={styles.stats}>
                    <span style={styles.stat}>
                      {blog.likes?.length || 0} Likes
                    </span>
                    <span style={styles.stat}>
                      {blog.comments?.length || 0} Comments
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "900px",
    margin: "0 auto",
    padding: "20px"
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "30px",
    borderBottom: "1px solid #ddd",
    paddingBottom: "15px"
  },
  headerButtons: {
    display: "flex",
    gap: "10px"
  },
  btn: {
    padding: "10px 20px",
    cursor: "pointer",
    border: "1px solid #333",
    background: "#fff"
  },
  addBtn: {
    padding: "10px 20px",
    cursor: "pointer",
    border: "none",
    background: "#28a745",
    color: "#fff"
  },
  main: {
    marginTop: "20px"
  },
  loading: {
    textAlign: "center",
    padding: "50px"
  },
  blogList: {
    display: "flex",
    flexDirection: "column",
    gap: "15px"
  },
  blogCard: {
    border: "1px solid #ddd",
    padding: "20px",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "box-shadow 0.2s"
  },
  description: {
    fontSize: "16px",
    marginBottom: "15px",
    lineHeight: "1.5"
  },
  blogMeta: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    color: "#666",
    fontSize: "14px"
  },
  author: {
    fontStyle: "italic"
  },
  stats: {
    display: "flex",
    gap: "15px"
  },
  stat: {
    background: "#f0f0f0",
    padding: "5px 10px",
    borderRadius: "4px"
  }
};
