import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";


const carouselImages = [
  { src: "/images/image1home.jpeg", alt: "Welcome to BlogBuzz" },
  { src: "/images/image2blog.jpeg", alt: "Share Your Stories" },
  { src: "/images/image3blog.jpeg", alt: "Connect with Others" }
];

export default function Home() {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentSlide, setCurrentSlide] = useState(0);
  const { user } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchBlogs();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % carouselImages.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  const fetchBlogs = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/blogs");
      const data = await res.json();
      setBlogs(data);
    } catch (error) {
      console.error("Error fetching blogs:", error);
    } finally {
      setLoading(false);
    }
  };

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % carouselImages.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + carouselImages.length) % carouselImages.length);
  };

  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const getShortDescription = (text, maxLength = 150) => {
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + "...";
  };

  if (loading) {
    return <div style={styles.loading}>Loading blogs...</div>;
  }

  return (
    <div style={styles.container}>
      <header style={styles.header}>
        <h1>‚úçüèºBlogBuzz</h1>
        <div style={styles.headerButtons}>
          {user ? (
            <>
              <button onClick={() => navigate("/add-blog")} style={styles.addBtn}>
                + Add Blog
              </button>
              <button onClick={() => navigate("/dashboard")} style={styles.btn}>
                Dashboard
              </button>
            </>
          ) : (
            <>
              <Link to="/login">
                <button style={styles.btn}>Login</button>
              </Link>
              <Link to="/register">
                <button style={styles.btn}>Signup</button>
              </Link>
            </>
          )}
        </div>
      </header>
      <div style={styles.carousel}>
        <div style={styles.carouselInner}>
          <img
            src={carouselImages[currentSlide].src}
            alt={carouselImages[currentSlide].alt}
            style={styles.carouselImage}
          />
          <div style={styles.carouselOverlay}>
            <h2 style={styles.carouselTitle}>{carouselImages[currentSlide].alt}</h2>
          </div>
        </div>
        <button onClick={prevSlide} style={styles.carouselBtnLeft}>
          &#10094;
        </button>

        {/* Next Button */}
        <button onClick={nextSlide} style={styles.carouselBtnRight}>
          &#10095;
        </button>

        {/* Dots */}
        <div style={styles.dotsContainer}>
          {carouselImages.map((_, index) => (
            <span
              key={index}
              style={index === currentSlide ? styles.dotActive : styles.dot}
              onClick={() => goToSlide(index)}
            />
          ))}
        </div>
      </div>

      <main style={styles.main}>
        <h2>All Blogs</h2>

        {blogs.length === 0 ? (
          <p>No blogs yet. Be the first to create one!</p>
        ) : (
          <div style={styles.blogList}>
            {blogs.map(blog => (
              <div
                key={blog._id}
                style={styles.blogCard}
                onClick={() => navigate(`/blog/${blog._id}`)}
              >
                <p style={styles.description}>
                  {getShortDescription(blog.description)}
                </p>
                <div style={styles.blogMeta}>
                  <span style={styles.author}>By: {blog.authorId?.name || "Unknown"}</span>
                  <div style={styles.stats}>
                    <span style={styles.stat}>
                      {blog.likes?.length || 0} Likes
                    </span>
                    <span style={styles.stat}>
                      {blog.comments?.length || 0} Comments
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}

const styles = {
  container: {
    maxWidth: "900px",
    margin: "0 auto",
    padding: "20px"
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: "30px",
    borderBottom: "1px solid #ddd",
    paddingBottom: "15px"
  },
  headerButtons: {
    display: "flex",
    gap: "10px"
  },
  btn: {
    padding: "10px 20px",
    cursor: "pointer",
    border: "1px solid #333",
    background: "#fff"
  },
  addBtn: {
    padding: "10px 20px",
    cursor: "pointer",
    border: "none",
    background: "#28a745",
    color: "#fff"
  },
  // Carousel Styles
  carousel: {
    position: "relative",
    width: "100%",
    height: "350px",
    marginBottom: "30px",
    borderRadius: "12px",
    overflow: "hidden",
    boxShadow: "0 4px 15px rgba(0,0,0,0.2)"
  },
  carouselInner: {
    width: "100%",
    height: "100%",
    position: "relative"
  },
  carouselImage: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
    transition: "opacity 0.5s ease-in-out"
  },
  carouselOverlay: {
    position: "absolute",
    bottom: "0",
    left: "0",
    right: "0",
    background: "linear-gradient(transparent, rgba(0,0,0,0.7))",
    padding: "40px 20px 20px",
    color: "#fff"
  },
  carouselTitle: {
    margin: "0",
    fontSize: "28px",
    fontWeight: "bold",
    textShadow: "2px 2px 4px rgba(0,0,0,0.5)"
  },
  carouselBtnLeft: {
    position: "absolute",
    top: "50%",
    left: "15px",
    transform: "translateY(-50%)",
    background: "rgba(255,255,255,0.8)",
    border: "none",
    padding: "15px 18px",
    fontSize: "20px",
    cursor: "pointer",
    borderRadius: "50%",
    transition: "background 0.3s"
  },
  carouselBtnRight: {
    position: "absolute",
    top: "50%",
    right: "15px",
    transform: "translateY(-50%)",
    background: "rgba(255,255,255,0.8)",
    border: "none",
    padding: "15px 18px",
    fontSize: "20px",
    cursor: "pointer",
    borderRadius: "50%",
    transition: "background 0.3s"
  },
  dotsContainer: {
    position: "absolute",
    bottom: "15px",
    left: "50%",
    transform: "translateX(-50%)",
    display: "flex",
    gap: "10px"
  },
  dot: {
    width: "12px",
    height: "12px",
    borderRadius: "50%",
    background: "rgba(255,255,255,0.5)",
    cursor: "pointer",
    transition: "background 0.3s"
  },
  dotActive: {
    width: "12px",
    height: "12px",
    borderRadius: "50%",
    background: "#fff",
    cursor: "pointer",
    transition: "background 0.3s"
  },
  main: {
    marginTop: "20px"
  },
  loading: {
    textAlign: "center",
    padding: "50px"
  },
  blogList: {
    display: "flex",
    flexDirection: "column",
    gap: "15px"
  },
  blogCard: {
    border: "1px solid #ddd",
    padding: "20px",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "box-shadow 0.2s"
  },
  description: {
    fontSize: "16px",
    marginBottom: "15px",
    lineHeight: "1.5"
  },
  blogMeta: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    color: "#666",
    fontSize: "14px"
  },
  author: {
    fontStyle: "italic"
  },
  stats: {
    display: "flex",
    gap: "15px"
  },
  stat: {
    background: "#f0f0f0",
    padding: "5px 10px",
    borderRadius: "4px"
  }
};
