import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

export default function Dashboard() {
  const [blogs, setBlogs] = useState([]);
  const [savedBlogs, setSavedBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState("my-blogs");

  const { user, logout, checkAuth } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    fetchMyBlogs();
    fetchSavedBlogs();
  }, []);

  const fetchMyBlogs = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/blogs/user/my-blogs", {
        credentials: "include",
      });
      if (res.ok) {
        const data = await res.json();
        setBlogs(data);
      }
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const fetchSavedBlogs = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/blogs/user/saved", {
        credentials: "include",
      });
      if (res.ok) {
        setSavedBlogs(await res.json());
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete this blog?")) return;
    const res = await fetch(`http://localhost:5000/api/blogs/${id}`, {
      method: "DELETE",
      credentials: "include",
    });
    if (res.ok) setBlogs(blogs.filter(b => b._id !== id));
  };

  const handleUnsave = async (id) => {
    const res = await fetch(`http://localhost:5000/api/blogs/${id}/save`, {
      method: "POST",
      credentials: "include",
    });
    if (res.ok) {
      setSavedBlogs(savedBlogs.filter(b => b._id !== id));
      checkAuth();
    }
  };

  const handleLogout = async () => {
    await logout();
    navigate("/");
  };

  const formatDate = (date) =>
    new Date(date).toLocaleDateString("en-US", {
      year: "numeric",
      month: "short",
      day: "numeric",
    });

  if (loading) return <div style={styles.loading}>Loading...</div>;

  return (
    <div style={styles.container}>
      {/* Header */}
      <header style={styles.header}>
        <h1>Dashboard</h1>
        <div>
          <button onClick={() => navigate("/")} style={styles.btn}>Home</button>
          <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
        </div>
      </header>

      {/* User Details */}
      <section style={styles.userSection}>
        <h2>User Details</h2>
        <div style={styles.userCard}>
          <p><strong>Name:</strong> {user?.name}</p>
          <p><strong>Email:</strong> {user?.email}</p>
          <p><strong>Role:</strong> {user?.role}</p>
          <p><strong>Member since:</strong> {user?.createdAt ? formatDate(user.createdAt) : "N/A"}</p>
        </div>
      </section>

      {/* Tabs */}
      <div style={styles.tabs}>
        <button
          style={activeTab === "my-blogs" ? styles.activeTab : styles.tab}
          onClick={() => setActiveTab("my-blogs")}
        >
          My Blogs ({blogs.length})
        </button>
        <button
          style={activeTab === "saved" ? styles.activeTab : styles.tab}
          onClick={() => setActiveTab("saved")}
        >
          Saved Blogs ({savedBlogs.length})
        </button>
      </div>

      {/* My Blogs */}
      {activeTab === "my-blogs" && (
        <section>
          <button onClick={() => navigate("/add-blog")} style={styles.addBtn}>
            + Add Blog
          </button>

          {blogs.map(blog => (
            <div key={blog._id} style={styles.blogCard}>
              <p>{blog.description}</p>
              <button onClick={() => handleDelete(blog._id)} style={styles.deleteBtn}>
                Delete
              </button>
            </div>
          ))}
        </section>
      )}

      {/* Saved Blogs */}
      {activeTab === "saved" && (
        <section>
          {savedBlogs.map(blog => (
            <div key={blog._id} style={styles.blogCard}>
              <p>{blog.description}</p>
              <button onClick={() => handleUnsave(blog._id)} style={styles.unsaveBtn}>
                Unsave
              </button>
            </div>
          ))}
        </section>
      )}
    </div>
  );
}
